import React, { Component } from "react";
import { Link } from "react-router-dom";


export default class MobileMenu extends Component {
  constructor(props) {
    super(props);
    this.state = {addClass: false}
  }
  toggle() {
    this.setState({addClass: !this.state.addClass});
  }
	render() {
		var shown = {
			display: this.state.shown ? "block" : "none"
		};

		var hidden = {
			display: this.state.shown ? "none" : "block"
		}

		return (
			<div className="container center">
				<h2 style={ shown }>this.state.shown = true</h2>
				<h2 style={ hidden }>this.state.shown = false</h2>


        <div id="mobileMenu" className="menu">
        <div className="wrapper">
            <div className="inner">
                    <nav className="mobilenav">
                            <ul>
                                <li className="index-item active">
                                    <span><a href="./index.html">Home</a></span>
                                </li> 
                                <li className="index-item">
                                    <span><a href="./services.html">Le Service</a></span>
                                </li>    
                                <li className="index-item">
                                    <span><a href="./pricing.html">Le Pricing</a></span>
                                </li> 
                                <li className="index-item">
                                    <span><a href="./about.html">Who is LeBeDATA</a></span>
                                </li> 
                                <li className="index-item">
                                        <span><a href="./contactus.html">Contact Us</a></span>
                                </li> 
                            </ul>
                        </nav>


            </div>
        </div> 
        <button id="hamburger" className="hamburger" onClick={this.toggle.bind(this)}>
            <p><span></span></p>
        </button>
         </div>
			</div>
		)
	}
}
